import React, { useRef, useEffect } from 'react';
import ImageMapper from 'react-img-mapper';

function Map({props}) {
    
    const mapRef = useRef(null);
    var containerWidth = null;
    const mapDesc = {
        name : 'my-map',
        areas : [{"id": "1", "title": "auditorium", "shape": "rect", "coords": [106,34,416,557]},
        {"id": "2", "title": "library", "shape": "poly", "coords": [420,35,420,240,616,243,618,156,651,156,653,37]},
        {"id": "3", "title": "hallway", "shape": "poly", "coords": [420,241,418,314,616,320,618,419,1281,429,1284,324,1205,320,1205,355,708,362,708,218,1203,218,1205,322,1465,324,1462,258,1286,252,1281,158,623,156,618,245]},
        {"id": "4", "title": "kitchen", "shape": "rect", "coords": [420,315,554,475]},
        {"id": "5", "title": "diningHall", "shape": "poly", "coords": [556,319,557,475,420,473,418,553,838,558,838,428,614,428,616,322]},
        {"id": "6", "title": "securityOffice", "shape": "rect", "coords": [728,37,925,153]},
        {"id": "7", "title": "entranceHall", "shape": "rect", "coords": [839,424,1094,734]},
        {"id": "8", "title": "nurseOffice", "shape": "poly", "coords": [927,37,927,149,1207,154,1203,104,1135,102,1135,39]},
        {"id": "9", "title": "mainOffice", "shape": "poly", "coords": [1096,428,1096,562,1388,560,1385,478,1286,480,1284,428]},
        {"id": "10", "title": "supplyRoom", "shape": "poly", "coords": [1136,39,1138,97,1203,101,1212,154,1348,156,1349,89,1460,89,1460,39]},
        {"id": "11", "title": "teacherLounge", "shape": "poly", "coords": [1353,92,1462,92,1462,250,1289,248,1287,163,1349,161]},
        {"id": "12", "title": "schoolStore", "shape": "rect", "coords": [1284,325,1390,475]},
        {"id": "13", "title": "gym", "shape": "poly", "coords": [1462,35,1749,36,1751,556,1390,554,1391,433,1464,433]},
        {"id": "14", "title": "atrium", "shape": "rect", "coords": [710,220,1203,357]},        ]
    }
    var map = document.getElementById('myMapId');
    var image = document.getElementById('myImageId');

    useEffect( () => {
        if(mapRef.current){
            containerWidth  = mapRef.current.offsetWidth;
            console.log(mapRef.current.offsetWidth);
        }
    }, [mapRef]);

        return(
            <div id ="mapOverlay">
                <div id="mapContainer">
                    <div className="mapMap" ref = {mapRef}>
                    <map name="image-map">
                        <area target="" alt="auditorium" title="auditorium" href="" coords="106,34,416,557" shape="rect"/>
                        <area target="" alt="library" title="library" href="" coords="420,35,420,240,616,243,618,156,651,156,653,37" shape="poly"/>
                        <area target="" alt="hallway" title="hallway" href="" coords="420,241,418,314,616,320,618,419,1281,429,1284,324,1205,320,1205,355,708,362,708,218,1203,218,1205,322,1465,324,1462,258,1286,252,1281,158,623,156,618,245" shape="poly"/>
                        <area target="" alt="kitchen" title="kitchen" href="" coords="420,315,554,475" shape="rect"/>
                        <area target="" alt="diningHall" title="diningHall" href="" coords="556,319,557,475,420,473,418,553,838,558,838,428,614,428,616,322" shape="poly"/>
                        <area target="" alt="securityOffice" title="securityOffice" href="" coords="728,37,925,153" shape="rect"/>
                        <area target="" alt="entranceHall" title="entranceHall" href="" coords="839,424,1094,734" shape="rect"/>
                        <area target="" alt="nurseOffice" title="nurseOffice" href="" coords="927,37,927,149,1207,154,1203,104,1135,102,1135,39" shape="poly"/>
                        <area target="" alt="mainOffice" title="mainOffice" href="" coords="1096,428,1096,562,1388,560,1385,478,1286,480,1284,428" shape="poly"/>
                        <area target="" alt="supplyRoom" title="supplyRoom" href="" coords="1136,39,1138,97,1203,101,1212,154,1348,156,1349,89,1460,89,1460,39" shape="poly"/>
                        <area target="" alt="teacherLounge" title="teacherLounge" href="" coords="1353,92,1462,92,1462,250,1289,248,1287,163,1349,161" shape="poly"/>
                        <area target="" alt="schoolStore" title="schoolStore" href="" coords="1284,325,1390,475" shape="rect"/>
                        <area target="" alt="gym" title="gym" href="" coords="1462,39,1749,40,1751,560,1390,558,1393,329,1467,332" shape="poly"/>
                        <area target="" alt="atrium" title="atrium" href="" coords="710,220,1203,357" shape="rect"/>
                    </map>
                        <ResponsiveImageMap map={map}/>
                    </div> 
                </div>
            </div>
        );
} export default Map;

class ResponsiveImageMap extends React.Component {
    constructor(props) {
        super(props);
        this.img = props.img;
        this.originalWidth = props.width;
        this.areas = [];

        for (const area of props.map.getElementsByTagName('area')) {
            this.areas.push({
                element: area,
                originalCoords: props.area.coords.split(',')
            });
        }

        window.addEventListener('resize', e => this.resize(e));
        this.resize();
    }

    resize() {
        const ratio = this.img.offsetWidth / this.originalWidth;

        for (const area of this.areas) {
            const newCoords = [];
            for (const originalCoord of area.originalCoords) {
                newCoords.push(Math.round(originalCoord * ratio));
            }
            area.element.coords = newCoords.join(',');
        }

        return true;
    };
}
